package com.cg;

import java.util.*;

public class AddressBookMain {
	public static void main(String[] args) {
        AddressBook addressBook = new AddressBook();
		AddressBookList addressBookList = new AddressBookList();
		Scanner in = new Scanner(System.in);
		System.out.println("Welcome");
		System.out.println("1. Open existing addressbook");
		System.out.println("2. Open new addressbook");
		System.out.println("3. Search persons in a city");
		System.out.println("4. Search persons in a state");
		System.out.println("5. Count persons in a city");
		System.out.println("6. Count persons in a state");
		System.out.println("7. Sort persons by city");
		System.out.println("8. Sort persons by state");
		System.out.println("9. Sort persons by zip");
		System.out.println("10. View all addressbooks");
		System.out.println("11. Exit");
		int option = in.nextInt();
		while(option != 3) {
			if(option == 1)
				addressBookList.openAddressBook();
			else if(option == 2)
				addressBookList.newAddressBook();
			else if(option == 3) {
			System.out.println("Enter City");
			String city = in.next();
			System.out.println(addressBook.searchPersonsByCity(city));
		}
		else if(option == 4) {
			System.out.println("Enter State");
			String state = in.next();
			System.out.println(addressBook.searchPersonsByState(state));
		}
		else if (option == 5) {
			System.out.println("Enter City");
			String city = in.next();
			System.out.println(addressBook.countPersonsByCity(city));
		}
		else if(option == 6) {
			System.out.println("Enter State");
			String state = in.next();
			System.out.println(addressBook.countPersonsByState(state));
		}
		else if (option == 7) {
			System.out.println(addressBook.sortPersonsByCity());
		}
		else if(option == 8) {
			System.out.println(addressBook.sortPersonsByState());
		}
		else if(option == 9) {
			System.out.println(addressBook.sortPersonsByZip());
		}
		else if(option == 10) {
			addressBookList.showDetails();
		}
		else if (option == 11)
				break;
			else{
	      		System.out.println("Error! Enter correct choice-");    
	      	}
	      	System.out.println("Enter choice again (1. Open existing addressbook, 2. Open new addressbook, 3. Search persons in a city,"
	      			+ "4. Search persons in a state, 5. Count persons in a city, 6. Count persons in a state, 7. Exit)-");
	      	option = in.nextInt();
	      }
		in.close();
		System.out.println("Thank You !");
	}
}